import React, { useEffect } from "react";
import { PayPalButtons, usePayPalScriptReducer } from "@paypal/react-paypal-js";
import { useDispatch, useSelector } from "react-redux";
import {
  paymentPaypalSuccess,
  updateSubscriptionId,
} from "../actions/userActions";

const ButtonPay = ({ type }) => {
  const [{ options }, dispatchPayPal] = usePayPalScriptReducer();
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);

  useEffect(() => {
    dispatchPayPal({
      type: "resetOptions",
      value: {
        ...options,
        intent: "subscription",
      },
    });
  }, [type, dispatchPayPal, options]);

  const handleApprove = (data, actions) => {
    dispatch(paymentPaypalSuccess());
  };

  const handleCreateSubscription = (data, actions) => {
    // Replace "YOUR_PLAN_ID" with the actual PayPal plan ID
    const planId = "YOUR_PLAN_ID";

    return actions.subscription
      .create({
        plan_id: planId,
      })
      .then((orderId) => {
        dispatch(updateSubscriptionId(userLogin, orderId));
        return orderId;
      });
  };

  return (
    <PayPalButtons
      createSubscription={handleCreateSubscription}
      onApprove={handleApprove}
      onError={(err) => console.error("PayPal Error:", err)}
      style={{
        label: "pay",
        color: "black",
        layout: "horizontal",
        height: 40, // Increase the height of the button
        width: "100%", // Set the width of the button to 100% of the container
      }}
    />
  );
};

export default ButtonPay;
